SingleEvent.create(:id => 2152, :description => "Am Mittwoch, den 15. Mai findet der 18. Twittwoch Ruhrgebiet im Unperfekthaus in Essen statt. Wie üblich startet der Twittwoch #twruhr um 19:00 Uhr.\r\n\r\nProgramm:\r\nFolgt in Kürze", :event_id => 297, :occurrence => Wed, 15 May 2013 19:00:00 CEST +02:00, :url => "https://www.xing.com/events/18-twittwoch-ruhrgebiet-twruhr-1240197", :full_day => false, :based_on_rule => false, :category_id => 1, :venue_id => 14, :use_venue_info_of_event => true)
SingleEvent.create(:id => 2154, :name => "Gründerfieber Spezial: 3 Day Startup - WrapUp", :description => "Treffe live ehemalige 3 Day Startup Teilnehmer, die aus dem 3 Day Startup Event heraus gegründet haben.\r\nNeben Tamyca, Reputami, Buy or Burn und Fine Cotten, welche erfolgreich aus dem 3 DS Event hervorgegangen sind, werden zahlreiche Gründer der Aachener lokalen Szene an diesem Abend die Podiumsdikusion verfolgen.\r\n\r\nErfahre wie es ist, innerhalb von 3 Tagen zum Unternehmer zu werden und mit welchen Hindernissen man rechnen muss aber auch welche Erfahrungen man bekommt und wie es nach 3 DS weitergegangen ist.\r\n\r\nDie Teilnahme ist natürlich wie immer kostenlos.\r\n\r\nIm Anschluss gehen wir wieder in den Gründerstammtisch über und alle Besucher sind herzlich eingeladen neue Kontakte zu knüpfen.\r\n\r\nTeilnehmer:\r\n\r\n- Michael Minis (tamyca)\r\n- Oliver Twardowski (reputami)\r\n- Tobias Hahn (Fine Cotten)\r\n- Clemens Walter (Buy or Burn)\r\n\r\nAnmeldung erforderlich!", :event_id => 316, :occurrence => Wed, 15 May 2013 19:00:00 CEST +02:00, :url => "http://aachenerentrepreneurshipteam.de/cms/?q=gruenderfieber-spezial-3ds-wrapup", :full_day => false, :based_on_rule => false, :category_id => 2, :venue_id => 242, :use_venue_info_of_event => true)
